// sum[x] - ile jest liczb pierwszych mniejszych od 100000 * x

bool prime(int n) {
	if (n < 2)
		return 0;
	// nie ma zaskoczenia - kazda liczba ujemna, ale takze 
	// zero i jedynka liczbami pierwszymi nie sa :D

	if (n % 2 == 0)
		return (n == 2);
	// przyspieszenie funkcji o polowe


	for (int i = 3; i * i <= n; i += 2)
		if (n % i == 0)
			return 0;

	return 1;
}

int main() {
	int n;
	cin >> n;

	if (n < 1e5) {
		int ile = 0;

		for (int i = 2; i < n; i ++)
			ile += prime(i);

		cout << ile;
		return 0;
	}
	// jezeli dana liczba na wejsciu jest mniejsza to nie unikne
	// w najgorszym przypadku ok. 100000 sprawdzen pierwszosci

	int zkr = 1;
	while (n >= zkr * 100000)
		zkr ++;
	// tutaj dostosowuje zakres liczby z dokladnoscia do 100000

	int ile = sum[zkr - 1];
	for (int i = (zkr - 1) * 100000 + 1; i < n; i ++)
		ile += prime(i);
	// a tutaj, bardzo czesty przypadek, musze sprawdzic ile liczb
	// znajduje sie po ostatnim pelnym zakresie

	cout << ile;
}